import React from 'react'
import { useContext, useState } from "react";
import { Context } from "../../context/Context"
import axios from "axios";

import SidebarSocietyMember from '../SidebarSocietyMember'


function UserProfile(props){

  let [updateSocietyMember,setUpdateSocietyMember]=useState({});

  let [SocietyMembers,setSocietyMembers]=useState([]);


  // function to read Value from form 

function readValue(property,value)
{
    setUpdateSocietyMember({...updateSocietyMember,[property]:value});
}

  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  
  const [username, setUsername] = useState("");
  const [address, setAddress] = useState("");
 
  const [password, setPassword] = useState("");
  const [contactNo, setContactNo] = useState("");
  const [success, setSuccess] = useState(false);
 
  const { user, dispatch } = useContext(Context);
  // const handleSubmit = async (e) => {
  //   e.preventDefault();
  //   dispatch({ type: "UPDATE_START" });
  //   const updateSocietyMember = {
  //     userId: user._id,
      
  //     fname,lname,  username,address, password,contactNo,
  //   };
  //   try {
  //     const res = await axios.put("http://localhost:8000/api/auth/SocietyMembers/" + user._id + updateSocietyMember);
  //     setSuccess(true);

  //     dispatch({ type: "UPDATE_SUCCESS", payload: res.data });
      
  //   } catch (err) {
  //     dispatch({ type: "UPDATE_FAILURE" });
  //   }
  // };


  function update()
{
    fetch(`http://localhost:8000/api/auth/SocietyMembers/${updateSocietyMember._id}`,{
        method:"PUT",
        headers:{
            "Content-Type":"application/json"
        },
        body:JSON.stringify(updateSocietyMember)
    })
    .then((response)=>response.json())
    .then((data)=>{

        let index=SocietyMembers.findIndex((user)=>user._id===updateSocietyMember._id);
        SocietyMembers[index]=updateSocietyMember;
        setSocietyMembers([...SocietyMembers]);
    alert("Society Member Updated Successfully")


    })
    .catch((err)=>{
        console.log(err);
    })
}


  return (
 
    <div className='SM-Side-User'>
        
    <SidebarSocietyMember/>
    
    

    <div className="SM-userPage">
    
    
      <div className=''>
      <h1>SocietyMember</h1>
          <span className="settingsUpdateTitle">Update Your Account</span>
          
        </div>
        <form className="App" onSubmit={update}>
          
        <label>First Name</label>
          <input
           type="text" defaultValue={updateSocietyMember.fname} className="form-control"
           onChange={(event)=>{
             readValue("fname",event.target.value);
           }}
           placeholder={user.fname}
            // onChange={(e) => setFname(e.target.value)}
          />
          <label>Last Name</label>
          <input
            type="text"
            placeholder={user.lname}
           defaultValue={updateSocietyMember.lname}  className="form-control"
                    onChange={(event)=>{
                      readValue("lname",event.target.value);
                  }}
            // onChange={(e) => setLname(e.target.value)}
          />
            
          
          <label>Username</label>
          <input
            type="text"
            placeholder={user.username}
            // onChange={(e) => setUsername(e.target.value)}
            defaultValue={updateSocietyMember.username}  className="form-control"
                    onChange={(event)=>{
                      readValue("username",event.target.value);
                  }}
          />

          <label>Address</label>
          <input
            type="text"
            placeholder={user.address}
            onChange={(e) => setAddress(e.target.value)}
          />
        
        
          <label>Password</label>
          <input
            type="password"
            onChange={(e) => setPassword(e.target.value)}
          />

          <label>Contact No</label>
          <input
            type="text"
            placeholder={user.contactNo}
            onChange={(e) => setContactNo(e.target.value)}
          />
        

          <button className="btn-create" type="submit">
            Update
          </button>
          {success && (
            <span
              style={{ color: "Blue", textAlign: "center", marginTop: "20px" }}
            >
              Profile has been updated...
            </span>
          )}
        </form>
   

</div>

  </div>
  
  )
}

export default UserProfile
