import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./register.css";
import {useNavigate} from 'react-dom'

import { signUpFormValidation } from '../../context/validation'


export default function Register() {


  const [user, setUser] = useState({

    username:"",
    email:"",
    password:"",

  });
  const [result, setResult] = useState({});


  // const [username, setUsername] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  const [error, setError] = useState(false);
  const [success , setSuccess] = useState(false);


  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    setResult(signUpFormValidation(user));

    setError(false);
    const {username,email, password} = user;
    try {

      const res = await axios.post("/auth/register", {

             body: JSON.stringify({ username,email, password })
      });

      const json = await res.json()
      console.log(json);
      if(json.success){
          // save the auth token and redirect
          localStorage.setItem('token', json.authtoken)
          navigate("/login")
          alert("Account successfully created", "success")
      }
      // alert("User Created Successfully");
      setSuccess(true);
      // res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);

    }
  };
  const changeHandler = (e) => {
    setUser({ ...user, [e.target.id]: e.target.value });
  };

  return (
    <div className="register">
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your username..."
          onChange={(e) =>  changeHandler(e)}
        />
         {result && <p style={{ color: "red" }}>{result.username}</p>}
        <label>Email</label>
        <input
          type="text"
          className="registerInput"
          id="email"
          placeholder="Enter your email..."
          onChange={(e) =>  changeHandler(e)}
        />
         {result && <p style={{ color: "red" }}>{result.email}</p>}
        <label>Password</label>
        <input

          type="password"
          className="registerInput"
          id="password"
          placeholder="Enter your password..."
          onChange={(e) =>  changeHandler(e)}
        />
         {result && <p style={{ color: "red" }}>{result.password}</p>}
        <button className="registerButton" type="submit">
          Register
        </button>
      </form>
      <button className="registerLoginButton">
        <Link className="link" to="/login">
          Login
        </Link>
      </button>
      {error && <span style={{color:"red", marginTop:"10px"}}>Something went wrong!</span>}
      {success && <span style={{color:"blue", marginTop:"10px"}}>User Created Successfully!</span>}
    </div>
  );
}
