import { createContext, useEffect, useReducer } from "react";
import Reducer from "./Reducer";

const INITIAL_STATE = {
  SocietyMember: JSON.parse(localStorage.getItem("SocietyMember")) || null,
  isFetching: false,
  error: false,
};

export const SMContext = createContext(INITIAL_STATE);

export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(Reducer, INITIAL_STATE);

  useEffect(() => {
    localStorage.setItem("SocietyMember", JSON.stringify(state.SocietyMember));
  }, [state.SocietyMember]);

  return (
    <SMContext.Provider
      value={{
        SocietyMember: state.SocietyMember,
        isFetching: state.isFetching,
        error: state.error,
        dispatch,
      }}
    >
      {children}
    </SMContext.Provider>
  );
};
