import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./register.css";


// import { signUpFormValidation } from '../../context/validation'


export default function Register() {


  const [inputValues, setInputValue] = useState({
    username: "",

    email: "",
    password: "",
  });

  const [validation, setValidation] = useState({
    username: "",

    email: "",
    password: "",

  });
  const [error, setError] = useState(false);
  const [success , setSuccess] = useState(false);

  function handleChange(event) {
    const { name, value } = event.target;
    setInputValue({ ...inputValues, [name]: value });
  }


  const checkValidation = () => {
    let errors = JSON.parse(JSON.stringify(validation));

    //first Name validation
    if (!inputValues.username.trim()) {
      errors.username = "user name is required";
    } else {
      errors.username = "";
    }

     // email validation
     const emailCond =
     "/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
   if (!inputValues.email.trim()) {
     errors.email = "Email is required";
   } else if (!inputValues.email.match(emailCond)) {
     errors.email = "Please ingress a valid email address";
   } else {
     errors.email = "";
   }

   //password validation
   const cond1 = "/^(?=.*[a-z]).{6,20}$/";
   const cond2 = "/^(?=.*[A-Z]).{6,20}$/";
   const cond3 = "/^(?=.*[0-9]).{6,20}$/";
   const password = inputValues.password;
   if (!password) {
     errors.password = "password is required";
   } else if (password.length < 6) {
     errors.password = "Password must be longer than 6 characters";
   } else if (password.length >= 20) {
     errors.password = "Password must shorter than 20 characters";
   } else if (!password.match(cond1)) {
     errors.password = "Password must contain at least one lowercase";
   } else if (!password.match(cond2)) {
     errors.password = "Password must contain at least one capital letter";
   } else if (!password.match(cond3)) {
     errors.password = "Password must contain at least a number";
   } else {
     errors.password = "";
   }

  //  //matchPassword validation
  //  if (!inputValues.confirmPassword) {
  //    errors.confirmPassword = "Password confirmation is required";
  //  } else if (inputValues.confirmPassword !== inputValues.Password) {
  //    errors.confirmPassword = "Password does not match confirmation password";
  //  } else {
  //    errors.password = "";
  //  }

   setValidation(errors);
 };

 useEffect(() => {
  checkValidation();
}, [inputValues]);


  const handleSubmit = async (e) => {
    e.preventDefault();



    setError(false);
    const { username, email,  password} = inputValues;
    try {
      const res = await axios.post("/auth/register", {

        headers: {
          'Content-Type': 'application/json'
           },
           body: JSON.stringify({username, email,  password})
      });
      alert("User Created Successfully");
      setSuccess(true);
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);

    }
  };

  return (
    <div className="register">
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit}>
      <div className="form-control">
        <label>Username</label>
        <input
          type="text"
          name="username"
          className="registerInput"
          placeholder="Enter your username..."
          onChange={(e) => handleChange(e)}
          value={inputValues.username}
          {...validation.username && <p>{validation.username}</p>}
          {...validation.username && console.log(validation)}
        />
       </div>

       <div className="form-control">
        <label>Email</label>
        <input
          type="email"
          name="email"
          className="registerInput"
          placeholder="Enter your email..."

          onChange={(e) => handleChange(e)}
          value={inputValues.email}
        />
       </div>
       {validation.email && <p>{validation.email}</p>}


       <div className="form-control">
        <label>Password</label>
        <input
        name="password"
          type="password"
          className="registerInput"
          placeholder="Enter your password..."
          onChange={(e) => handleChange(e)}
          value={inputValues.password}
              required
        />
         {validation.password && <p>{validation.password}</p>}
        </div>

        <button className="registerButton" type="submit">
          Register
        </button>
      </form>
      <button className="registerLoginButton">
        <Link className="link" to="/login">
          Login
        </Link>
      </button>
      {error && <span style={{color:"red",fontSize:"20px",marginTop:"10px"}}>Something went wrong!</span>}
      {success && <span style={{color:"blue", marginTop:"10px"}}>User Created Successfully!</span>}
    </div>
  );
}
