import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./register.css";


import { signUpFormValidation } from '../../context/validation'


export default function Register() {

  const [user, setUser] = useState( {});
  const [result, setResult] = useState({});

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);
  const [success , setSuccess] = useState(false);



  const handleSubmit = async (e) => {
    e.preventDefault();

    setResult(signUpFormValidation(user));

    setError(false);
    try {
      const res = await axios.post("/auth/register", {
        username,
        email,
        password,
      });
      alert("User Created Successfully");
      setSuccess(true);
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);

    }
  };
  const changeHandler = (e) => {
    setUsername({ ...user, [e.target.id]: e.target.value });
    setPassword({ ...user, [e.target.id]: e.target.value });
    setEmail({ ...user, [e.target.id]: e.target.value });
  };

  return (
    <div className="register">
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your username..."
          onChange={(e) => changeHandler(e.target.value)}
        />
        <label>Email</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your email..."
          onChange={(e) => changeHandler(e.target.value)}
        />
         {result && <p style={{ color: "red" }}>{result.email}</p>}
        <label>Password</label>
        <input

          type="password"
          className="registerInput"
          placeholder="Enter your password..."
          onChange={(e) => changeHandler(e.target.value)}
        />
        <button className="registerButton" type="submit">
          Register
        </button>
      </form>
      <button className="registerLoginButton">
        <Link className="link" to="/login">
          Login
        </Link>
      </button>
      {error && <span style={{color:"red", marginTop:"10px"}}>Something went wrong!</span>}
      {success && <span style={{color:"blue", marginTop:"10px"}}>User Created Successfully!</span>}
    </div>
  );
}
