import axios from "axios";
import { useContext, useRef, useEffect,useState } from "react";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";
import "./login.css";

export default function Login() {
  const userRef = useRef();
  const passwordRef = useRef();
  const { dispatch, isFetching } = useContext(Context);
  const [error, setError] = useState(false);


  const [user, setUser] = useState({
    username: "",


    password: "",
  });

  const [validation, setValidation] = useState({
    username: "",


    password: "",

  });

  const handleChange =(e) => {
    setUser({...user, [e.target.name]: e.target.value})
  }


  const checkValidation = () => {
    let errors = JSON.parse(JSON.stringify(validation));

    //first Name validation
    const username = user.username;
    if (!user.username.trim()) {
      errors.username = "Username is required";
    } else if (username.length < 6) {
      errors.username = "Username must be longer than 4  characters";
    }else {
      errors.username = "";
    }



   //password validation

   const password = user.password;
   if (!password) {
     errors.password = "Password is required";
   } else {
     errors.password = "";
   }



   setValidation(errors);
 };

 useEffect(() => {
  checkValidation();
}, [user]);


  const handleSubmit = async (e,req) => {
    e.preventDefault();
    dispatch({ type: "LOGIN_START" });
    // const { username,   password} = inputValues;
    try {
      const res = await axios.post("/auth/login", {
        // username: userRef.current.value,
        // password: passwordRef.current.value,
        // username,
        // password,

        headers: {
          'Content-Type': 'application/json'
           },
           body : JSON.stringify({username:user.username,password:user.password })


      })

      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
      alert("User Logged in Successfully");
    } catch (err) {
      dispatch({ type: "LOGIN_FAILURE" });
      setError(true);
      console.log(error);
      alert(error);
      // alert("Username Is not valid");
      // alert("Password does not Match");

    }
  };

  return (
    <div className="login">
      <span className="loginTitle">Login</span>
      <div className="loginForm-BG">
      <form className="loginForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          type="text"
          className="loginInput"
          placeholder="Enter your username..."
          // ref={userRef}
          onChange={(e) => handleChange(e)}
          value={user.username} 
          name="username"
          required
        />
          {validation.username &&  <span style={{color:"red",paddingLeft:"10px",fontSize:"18px", marginTop:"10px"}}><p>{validation.username}</p></span>}
        <label>Password</label>
        <input
          type="password"
          className="loginInput"
          placeholder="Enter your password..."
          // ref={passwordRef}
          onChange={(e) => handleChange(e)}
          value={user.password}
          name="password"
              required

        />
              {validation.password &&  <span style={{color:"red",paddingLeft:"10px",fontSize:"18px", marginTop:"10px"}}><p>{validation.password}</p></span> }

        <button className="loginButton" type="submit" disabled={isFetching}>
          Login
        </button>
        {error && <span style={{color:"rgb(255, 0, 0)",fontSize:"20px", marginTop:"10px"}}>Invalid Credentials!</span>}
      </form>
      </div>
      <button className="loginRegisterButton">
        <Link className="link" to="/register">
          Register
        </Link>
      </button>
    </div>
  );
}
