import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom";
import "./register.css";
import { useForm } from 'react-hook-form';
import { Form, Button } from 'react-bootstrap';

// import { signUpFormValidation } from '../../context/validation'


export default function Register() {


  const { register, handleSubmit, errors } = useForm();


  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(false);
  const [success , setSuccess] = useState(false);



  const onSubmit = async (e) => {
    e.preventDefault();



    setError(false);
    try {
      const res = await axios.post("/auth/register", {
        username,
        email,
        password,
      })
       .then(response => res.json())
       .then(data => {
         if(data.error) throw Error(data.error)

       })
       .catch( error => {alert( error)

   })   


      alert("User Created Successfully");
      setSuccess(true);
      res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);

    }
  };

  return (
    <div className="register">
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit(onSubmit)}>
        <label>Username</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your username..."
          onChange={(e) => setUsername(e.target.value)}
        />

        <label>Email</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your email..."

          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Password</label>
        <input

          type="password"
          className="registerInput"
          placeholder="Enter your password..."
          onChange={(e) => setPassword(e.target.value)}
        />
        <button className="registerButton" type="submit">
          Register
        </button>
      </form>
      <button className="registerLoginButton">
        <Link className="link" to="/login">
          Login
        </Link>
      </button>
      {error && <span style={{color:"red",fontSize:"20px",marginTop:"10px"}}>Something went wrong!</span>}
      {success && <span style={{color:"blue", marginTop:"10px"}}>User Created Successfully!</span>}
    </div>
  );
}
