import React, { Component } from 'react';

class Register extends Component {

 constructor() {
 super();
 this.state = {
 formFields: {
 username: '',

 email: '',
 password: ''
 },
 errors: {}
 }

 // To bind different events
 this.handleChange = this.handleChange.bind(this);
 this.handleSubmit = this.handleSubmit.bind(this);
 };

 // When any field value changed
 handleChange(event) {
 let formFields = this.state.formFields;
 formFields[event.target.name] = event.target.value;
 this.setState({
 formFields
 });
 }

 // To validate all form fields
 validate() {
 let formFields = this.state.formFields;
 let errors = {};
 let IsValid = true;

 if (!formFields["username"]) {
 IsValid = false;
 errors["username"] = "Enter Your Username";
 }

 // To allow only alphabets value
 if (typeof formFields["username"] !== "undefined") {
 if (!formFields["username"].match(/^[a-zA-Z ]*$/)) {
 IsValid = false;
 errors["username"] = "Only Alphabet Characters Are Allowed";
 }
 }



 if (!formFields["email"]) {
 IsValid = false;
 errors["email"] = "Enter Your Email";
 }

 // To allow valid email format
 if (typeof formFields["email"] !== "undefined") {
 var pattern = new RegExp(/^[a-z][a-zA-Z0-9_]*(\.[a-zA-Z][a-zA-Z0-9_]*)?@[a-z][a-zA-Z-0-9]*\.[a-z]+(\.[a-z]+)?$/);
 if (!pattern.test(formFields["email"])) {
 IsValid = false;
 errors["email"] = "Invalid Email";
 }
 }

 if (!formFields["password"]) {
 IsValid = false;
 errors["password"] = "Enter The Password";
 }

 // Should have valid password format
 if (typeof formFields["password"] !== "undefined") {
 if (!formFields["password"].match("^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#\$%\^&\*])(?=.{8,})")) {
 IsValid = false;
 errors["password"] = "Enter Valid Password";
 }
 }

 this.setState({
 errors: errors
 });
 return IsValid;
 }

