import React from 'react'
import { useContext, useState } from "react";
import { Context } from "../../context/Context"


import SidebarSocietyMember from '../SidebarSocietyMember'


function UserProfile(props){


  let [SocietyMembers,setSocietyMembers]=useState({user});

    let [SocietyMembersCopy,setSocietyMembersCopy]=useState({user});

    // // variable to store user while updating 
    let [updateSocietyMember,setUpdateSocietyMember]=useState({user});

  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  
  const [username, setUsername] = useState("");
  const [address, setAddress] = useState("");
 
  const [password, setPassword] = useState("");
  const [contactNo, setContactNo] = useState("");
  const [success, setSuccess] = useState(false);
 
  const { user, dispatch } = useContext(Context);
  const handleSubmit = async (e) => {
    e.preventDefault();
    fetch(`http://localhost:8000/api/auth/SocietyMembers/${updateSocietyMember._id}`,{
        method:"PUT",
        headers:{
            "Content-Type":"application/json"
        },
        body:JSON.stringify(updateSocietyMember)
    })
    .then((response)=>response.json())
    .then((data)=>{

        let index=SocietyMembers.findIndex((user)=>user._id===updateSocietyMember._id);
        SocietyMembers[index]=updateSocietyMember;
        setSocietyMembers([...SocietyMembers]);
    alert("Society Member Updated Successfully")


    })
    .catch((err)=>{
        console.log(err);
    })
  };


  


  return (
 
    <div className='SM-Side-User'>
        
    <SidebarSocietyMember/>
    
    

    <div className="SM-userPage">
    
    
      <div className=''>
      <h1>SocietyMember</h1>
          <span className="settingsUpdateTitle">Update Your Account</span>
          
        </div>
        <form className="App" onSubmit={handleSubmit}>
          
        <label>First Name</label>
          <input
           type="text"className="form-control"
          
           placeholder={user.fname}
            onChange={(e) => setFname(e.target.value)}
          />
          <label>Last Name</label>
          <input
            type="text"
            placeholder={user.lname}
          className="form-control"
                   
            onChange={(e) => setLname(e.target.value)}
          />
            
          
          <label>Username</label>
          <input
            type="text"
            placeholder={user.username}
            onChange={(e) => setUsername(e.target.value)}
             className="form-control"
                   
          />

          <label>Address</label>
          <input
            type="text"
             className="form-control"
                  
            placeholder={user.address}
            onChange={(e) => setAddress(e.target.value)}
          />
        
        
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            
            onChange={(e) => setPassword(e.target.value)}
          />

          <label>Contact No</label>
          <input
            type="text"
             className="form-control"
           
            placeholder={user.contactNo}
            onChange={(e) => setContactNo(e.target.value)}
          />
        

          <button className="btn-create" type="submit">
            Update
          </button>
          {success && (
            <span
              style={{ color: "Blue", textAlign: "center", marginTop: "20px" }}
            >
              Profile has been updated...
            </span>
          )}
        </form>
   

</div>

  </div>
  
  )
}

export default UserProfile
